item.sql:
WITH t AS (SELECT DISTINCT '' || (agd_id) id, agd_name name, agd_parent_id pid, 'true' nocheck, agd_key flag, '0' type, 'roles.png' icon FROM sys_auth_group_dtls WHERE agd_type = 2
START WITH agd_id = (SELECT agd_id FROM sys_auth_group_dtls WHERE agd_key = :agkey) CONNECT BY PRIOR agd_id = agd_parent_id
) SELECT '1-' || rbd_id id, rbd_name name, rbd_group_id pid, null nocheck, null flag, '1' type, 'role.png' icon FROM sys_role_base_dtls INNER JOIN t ON rbd_group_id = t.id UNION ALL SELECT id, name, pid, nocheck, flag, type, icon FROM t
